import { Component } from '@angular/core';
import { HomeServicesService } from '../homeServices/home-services.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent {
  userData: any[] = [];
  constructor(private userServ: HomeServicesService) {}

  ngOnInit() {
    this.getAll();
  }

  getAll() {
    this.userServ.getAllDataUser().subscribe((data: any) => {
      //    this.userData =  Object.values(data);
      this.userData = data;
      console.log(this.userData);
    });
  }
}
<table class="table mt-5">
  <thead>
    <tr>
      <th scope="col">Id</th>
      <th scope="col">First Name</th>
      <th scope="col">Last Name</th>
      <th scope="col">Image</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">{{userData.data.id}}</th>
      <td>{{userData.first_name}}</td>
      <td>{{userData.last_name}}</td>
      <td><img [src]="userData.avatar" alt=""></td>
    </tr>

  </tbody>
</table>
