import { Component } from '@angular/core';
import { Location } from '@angular/common';

import { ActivatedRoute } from '@angular/router';
import { HomeServicesService } from '../home/homeServices/home-services.service';

@Component({
  selector: 'app-user-details',
  templateUrl: './user-details.component.html',
  styleUrls: ['./user-details.component.css'],
})
export class UserDetailsComponent {
  id: any;
  detailsProduct: any = {};
  loaderPage: boolean = false;
  constructor(
    private location: Location,
    private route: ActivatedRoute,
    private serv: HomeServicesService
  ) {
    this.id = this.route.snapshot.paramMap.get('id');
    console.log(this.id);
  }
  ngOnInit() {
    this.getDetails();
  }
  getDetails() {
    this.loaderPage = true;
    this.serv.getOneUserInformation(this.id).subscribe((data: any) => {
      this.detailsProduct = data.data;
      console.log(this.detailsProduct);
      this.loaderPage = false;
    });
  }
  goBack() {
    this.location.back();
  }
}
<div id="loader-page" *ngIf="loaderPage">
  <div class="loader"></div>
</div>
<div class="all mt-5">
  <div class="container">
    <div class="row">
      <div class="col-md-6">
        <img class="w-75" [src]="detailsProduct.avatar" alt="">
      </div>
      <div class="col-md-6">
        <ul>
          <li>ID : <span>{{detailsProduct.id}}</span>
          </li>
          <li>First Name : <span>{{detailsProduct.first_name}}</span></li>
          <li>Last Name : <span>{{detailsProduct.last_name}}</span></li>
          <li>Email :<span>{{detailsProduct.email}}</span></li>
        </ul>
      </div>
      <div class="col-md-6 mt-5">
        <button type="button" (click)="goBack()" class="btn btn-warning">Back</button>

      </div>
    </div>
  </div>
</div>
