import { Component } from '@angular/core';
import { HomeServicesService } from '../homeServices/home-services.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent {
  userData: any[] = [];
  loaderPage: boolean = false;
  filterName: string = '';

  constructor(private userServ: HomeServicesService) {}

  ngOnInit() {
    this.getAll();
    this.userServ.filterName$.subscribe((filterName: string) => {
      this.filterName = filterName;
    });
  }

  getAll() {
    this.loaderPage = true;
    this.userServ.getAllDataUser().subscribe((data: any) => {
      this.userData = data.data;
      this.loaderPage = false;
      //   console.log(this.userData);

      /*   if (data  && data.data && Array.isArray(data.data)) {
    this.userData = data.data;
  } else {
    console.error('Unexpected response structure:', data);
  } */
    });
  }
}
<div id="loader-page" *ngIf="loaderPage">
  <div class="loader"></div>
</div>
<div class="form-group mt-5">
  <!--   <input type="text" class="form-control" placeholder="Enter User ID" id="email" autocomplete="off" [(ngModel)]="filterName">
 -->
</div>
<table class="table mt-5">
  <thead>
    <tr>
      <th scope="col">Id</th>
      <th scope="col">First Name</th>
      <th scope="col">Last Name</th>
      <th scope="col">Image</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let item of userData | search:filterName " routerLink="/details/{{item.id}}">
      <th scope="row">{{item.id}}</th>
      <td>{{item.first_name}}</td>
      <td>{{item.last_name}}</td>
      <td><img [src]="item.avatar" alt=""></td>
    </tr>

  </tbody>
</table>
